import { Component } from '@angular/core';
import { render } from "creditcardpayments/creditCardPayments";

@Component({
  selector: 'app-paypal-screen',
  templateUrl: './paypal-screen.component.html',
  styleUrls: ['./paypal-screen.component.css']
})
export class PaypalScreenComponent {
  constructor() {
    render ({
      id: "#myPaypalBtns",
      currency: "USD",
      value: "120.00",
      onApprove: (details) => {
        alert("Transaction successful!");
      }
    });
  }
}
<div class="main">
  <!-- Button trigger modal -->
<button type="button" class="btn btn-primary btn-floating btn-lg" data-mdb-toggle="modal" data-mdb-target="#myModal">
  <i class='bx bxl-paypal'></i>
</button>

 <!-- Modal -->
 <div class="modal fade" id="myModal" tabindex="-1" aria-labelledby="exampleModalLabel"
   aria-hidden="true">
   <div class="modal-dialog">
     <div class="modal-content">
       <div class="modal-header">
         <h5 class="modal-title" id="exampleModalLabel">Payment Gateway</h5>
         <button type="button" class="btn-close" data-mdb-dismiss="modal" aria-label="Close"></button>
       </div>
       <div class="modal-body">
        <div class="paypal-container" id="myPaypalBtns">

        </div>
       </div>
     </div>
   </div>
 </div>
</div>
